package com.basic.java.string;

public class ConcatenatingUsingPluseOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String s = "Hello";
    	String str = "Java";
    	String str1 = s+str;
    	String str2 = "Java"+11;
    	System.out.println(str1);
    	System.out.println(str2);
	}

}
