package com.basic.java.string;

public class StringUpperCase {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String data="KannaTHAsan";

		System.out.println("Before Uppercase " +data);

		String dataUpper = data.toUpperCase();

		System.out.println("After Upper " +dataUpper);


	}

}
